public class ForEver {
    /**
     * @param args
     */
    public static void main(String[] args) {
        // TODO Auto-generated method stub
        /* Program */
        System.out.println("Program akan looping, akhiri dengan ^c");

        // Infinite loop
        while (true) {
            System.out.print("Print satu baris\n");
        }
    }
}
